define(['core/modal_factory','core/modal_events'],function(a,b){return{init:function(){document.querySelectorAll('.friction-info').forEach(function(b){b.addEventListener('click',function(){const c=b.textContent,d=b.dataset.score,e=b.dataset.explanation,f=b.dataset.what,g=b.dataset.action;a.create({title:c,body:'<p><strong>Score:</strong> '+d+'</p>'+'<p>'+e.replace(/\n/g,'<br>')+'</p>'+'<p><b>'+f.replace(/\n/g,'<br>')+'</b></p>'+'<p>'+g.replace(/\n/g,'<br>')+'</p>'}).then(function(a){a.show()})})})}}})